import { createServerClient } from '@supabase/ssr';
import { cookies } from 'next/headers';
import { NextRequest, NextResponse } from 'next/server';
import { stripe } from '@/lib/stripe'; // Assuming stripe client is configured
import { getErrorRedirect, getStatusRedirect } from '@/utils/helpers'; // Assumed helpers
import { Database } from '@/types/supabase'; // Assuming generated types
import { createAdminClient } from '@/lib/supabase/admin'; // Assuming admin client for inserts
import { createWebhookIfMissing } from '@/lib/stripe/webhooks';
import { enqueueBackfill } from '@/lib/guardian/backfill';

// TODO: Import actual functions when created
// import { enqueueBackfill } from '@/lib/guardian/backfill';

export async function GET(request: NextRequest) {
  const requestUrl = new URL(request.url);
  const code = requestUrl.searchParams.get('code');
  const error = requestUrl.searchParams.get('error');
  const errorDescription = requestUrl.searchParams.get('error_description');

  const cookieStore = cookies();
  const supabase = createServerClient<Database>(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    { cookies: { get: (name) => cookieStore.get(name)?.value } },
  );

  // 1. Handle OAuth errors from Stripe
  if (error) {
    console.error(`Stripe OAuth Error: ${error} - ${errorDescription}`);
    return NextResponse.redirect(getErrorRedirect(requestUrl.origin, error, errorDescription));
  }

  // 2. Handle missing code
  if (!code) {
    console.error('Missing OAuth code in callback');
    return NextResponse.redirect(
      getErrorRedirect(requestUrl.origin, 'OAuth Error', 'No code returned from Stripe.'),
    );
  }

  try {
    // 3. Exchange code for tokens
    console.log('Exchanging OAuth code for Stripe tokens...');
    const response = await stripe.oauth.token({
      grant_type: 'authorization_code',
      code: code,
    });
    console.log('Stripe token exchange successful:', response.stripe_user_id);

    const stripeAccountId = response.stripe_user_id;
    const refreshToken = response.refresh_token; // Store securely!
    const accessToken = response.access_token; // Store securely!
    const scope = response.scope;

    if (!stripeAccountId) {
      throw new Error('Stripe account ID not found in OAuth response.');
    }

    // 4. Get Supabase User
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();
    if (userError || !user) {
      console.error('Error fetching user:', userError);
      return NextResponse.redirect(
        getErrorRedirect(requestUrl.origin, 'Authentication Error', 'Could not get user session.'),
      );
    }
    console.log('Supabase user found:', user.id);

    // Use Admin client for DB operations from server-side route
    const supabaseAdmin = createAdminClient();

    // 5. Check account limit (<= 2)
    console.log(`Checking account limit for user ${user.id}...`);
    const { count, error: countError } = await supabaseAdmin
      .from('stripe_accounts') // Assuming this table exists
      .select('id', { count: 'exact', head: true })
      .eq('user_id', user.id);

    if (countError) {
      console.error('Error counting Stripe accounts:', countError);
      throw new Error('Could not verify account limit.'); // Internal error
    }

    const MAX_ACCOUNTS = 2;
    if (count !== null && count >= MAX_ACCOUNTS) {
      console.warn(`User ${user.id} has reached account limit (${count}/${MAX_ACCOUNTS}).`);
      return NextResponse.redirect(
        getErrorRedirect(
          requestUrl.origin,
          'Account Limit Reached',
          `You can connect a maximum of ${MAX_ACCOUNTS} Stripe accounts.`,
          '/dashboard', // Redirect back to dashboard or settings
        ),
      );
    }
    console.log(`User ${user.id} has ${count ?? 0} accounts, proceeding.`);

    // 6. Insert/Update Stripe Account Info (encrypt tokens!)
    const keyId = process.env.SODIUM_ENCRYPTION_KEY_ID;
    if (!keyId) {
      console.error('SODIUM_ENCRYPTION_KEY_ID is not set in environment variables.');
      throw new Error('Server configuration error for encryption.');
    }

    console.log(`Upserting Stripe account ${stripeAccountId} for user ${user.id}...`);
    // We need to use raw SQL for encryption as Supabase client doesn't directly support pgsodium bytea functions yet.
    // Note: Ensure pgsodium is enabled and the key referenced by keyId exists.
    const { error: upsertError } = await supabaseAdmin.rpc('upsert_stripe_account', {
      p_user_id: user.id,
      p_stripe_account_id: stripeAccountId,
      p_scope: scope,
      p_refresh_token: refreshToken, // Pass plaintext to function
      p_access_token: accessToken, // Pass plaintext to function
      p_key_id: keyId,
    });

    // We need to define the upsert_stripe_account SQL function in a migration
    // This function will handle the encryption using pgsodium.crypto_aead_det_encrypt

    if (upsertError) {
      console.error('Error upserting Stripe account:', upsertError);
      throw new Error(`Failed to save Stripe account details: ${upsertError.message}`);
    }
    console.log(`Successfully upserted Stripe account ${stripeAccountId}`);

    // 7. Provision Webhook
    console.log(`Provisioning webhook for account ${stripeAccountId}...`);
    await createWebhookIfMissing(stripeAccountId);
    console.log(`Webhook provisioning step completed for ${stripeAccountId}.`);

    // 8. Enqueue Backfill & Create initial status record
    console.log(`Enqueueing backfill for account ${stripeAccountId}...`);
    await enqueueBackfill(user.id, stripeAccountId);
    console.log(`Backfill enqueue step completed for ${stripeAccountId}.`);

    // 9. Redirect on success
    console.log(`Redirecting to dashboard for user ${user.id}, account ${stripeAccountId}`);
    return NextResponse.redirect(
      getStatusRedirect(
        requestUrl.origin,
        'Stripe Account Connected',
        `Successfully connected ${stripeAccountId}. Historical data back-fill initiated.`,
      ),
    );
  } catch (err: any) {
    console.error('Error during Stripe OAuth callback:', err);
    const message = err.message || 'An unexpected error occurred during Stripe Connect.';
    return NextResponse.redirect(getErrorRedirect(requestUrl.origin, 'Connection Failed', message));
  }
}

'use client';

import { useState, useTransition, ChangeEvent } from 'react';
import { toast } from 'sonner';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogClose,
} from '@/components/ui/dialog';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Label } from '@/components/ui/label';
import { Trash2, PlusCircle, Copy } from 'lucide-react';
import { ApiKeyInfo } from '@/lib/supabase/user'; // Import type
import { generateApiKeyServerAction, revokeApiKeyServerAction } from './actions'; // Import the real server actions
import { Card, CardContent, CardFooter, CardDescription } from '@/components/ui/card';

interface ApiKeysManagerProps {
  initialApiKeys: ApiKeyInfo[];
}

export function ApiKeysManager({ initialApiKeys }: ApiKeysManagerProps) {
  const [apiKeys, setApiKeys] = useState<ApiKeyInfo[]>(initialApiKeys);
  const [newKeyName, setNewKeyName] = useState('');
  const [generatedKey, setGeneratedKey] = useState<string | null>(null);
  const [isGenerating, startGeneratingTransition] = useTransition();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [showRevokeDialog, setShowRevokeDialog] = useState(false);
  const [keyToRevoke, setKeyToRevoke] = useState<ApiKeyInfo | null>(null);

  const handleGenerateKey = () => {
    if (!newKeyName.trim()) {
      toast.error('Please enter a name for the API key.');
      return;
    }
    startGeneratingTransition(async () => {
      const formData = new FormData();
      formData.append('keyName', newKeyName);
      const result = await generateApiKeyServerAction(formData);

      if (result.success && result.apiKey) {
        toast.success('API Key generated successfully!');
        setGeneratedKey(result.apiKey);
        // Optimistically add (or wait for re-fetch/invalidation)
        // For simplicity, let's assume revalidation will handle list update
        // Or manually refetch profile data here
        setNewKeyName(''); // Clear input
        // Keep dialog open to show the key
      } else {
        toast.error(`Error generating API key: ${result.error || 'Unknown error'}`);
        setIsCreateDialogOpen(false); // Close dialog on error
      }
    });
  };

  const handleRevokeKey = (keyId: string) => {
    const revoke = async () => {
      const formData = new FormData();
      formData.append('keyId', keyId);
      const result = await revokeApiKeyServerAction(formData);

      if (result.success) {
        toast.success('API Key revoked successfully!');
        setApiKeys((currentKeys) => currentKeys.filter((key) => key.id !== keyId));
      } else {
        toast.error(`Error revoking API key: ${result.error || 'Unknown error'}`);
      }
    };
    revoke();
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard
      .writeText(text)
      .then(() => toast.success('API Key copied to clipboard!'))
      .catch((_err) => toast.error('Failed to copy key.'));
  };

  const closeCreateDialog = () => {
    setIsCreateDialogOpen(false);
    setGeneratedKey(null); // Clear generated key when dialog closes
    setNewKeyName('');
  };

  const handleRevoke = (keyId: string, keyName: string) => {
    setKeyToRevoke({ id: keyId, name: keyName });
    setShowRevokeDialog(true);
  };

  const confirmRevoke = () => {
    if (keyToRevoke) {
      handleRevokeKey(keyToRevoke.id);
      setShowRevokeDialog(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Key List */}
      <div className="rounded-md border">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Name</TableHead>
              <TableHead>Prefix</TableHead>
              <TableHead>Created</TableHead>
              <TableHead className="text-right">Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {apiKeys.length === 0 && (
              <TableRow>
                <TableCell colSpan={4} className="h-24 text-center text-muted-foreground">
                  No API keys created yet.
                </TableCell>
              </TableRow>
            )}
            {apiKeys.map((key) => (
              <TableRow key={key.id}>
                <TableCell className="font-medium">{key.name}</TableCell>
                <TableCell>{key.prefix}****</TableCell> {/* Show prefix only */}
                <TableCell>{new Date(key.created_at).toLocaleDateString()}</TableCell>
                <TableCell className="text-right">
                  <div className="flex items-center space-x-2">
                    <Button onClick={() => handleRevoke(key.id, key.name)} variant="destructive">
                      <Trash2 className="mr-2 h-4 w-4" /> Revoke
                    </Button>

                    {/* Confirmation Dialog */}
                    <AlertDialog open={showRevokeDialog && keyToRevoke?.id === key.id}>
                      <AlertDialogContent>
                        <AlertDialogHeader>
                          <AlertDialogTitle>Revoke API Key?</AlertDialogTitle>
                          <AlertDialogDescription>
                            Are you sure you want to revoke the key named &quot;{keyToRevoke?.name}
                            &quot;? This action cannot be undone.
                          </AlertDialogDescription>
                        </AlertDialogHeader>
                        <AlertDialogFooter>
                          <AlertDialogCancel onClick={() => setShowRevokeDialog(false)}>
                            Cancel
                          </AlertDialogCancel>
                          <AlertDialogAction onClick={confirmRevoke} variant="destructive">
                            Revoke Key
                          </AlertDialogAction>
                        </AlertDialogFooter>
                      </AlertDialogContent>
                    </AlertDialog>
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      {/* Create Key Button & Dialog */}
      <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
        <DialogTrigger asChild>
          <Button disabled={isGenerating}>
            <PlusCircle className="mr-2 h-4 w-4" /> Create New API Key
          </Button>
        </DialogTrigger>
        <DialogContent
          onInteractOutside={(e) => {
            // Prevent closing if we just generated a key to show
            if (generatedKey) e.preventDefault();
          }}
        >
          {generatedKey ? (
            <>
              <DialogHeader>
                <DialogTitle>API Key Generated</DialogTitle>
                <DialogDescription>
                  Your new API key has been created. Please copy it now, as you won't be able to see
                  it again.
                </DialogDescription>
              </DialogHeader>
              <div className="flex items-center space-x-2 mt-4 bg-muted p-3 rounded-md">
                <Input
                  id="new-api-key"
                  value={generatedKey}
                  readOnly
                  className="flex-1 font-mono text-sm"
                />
                <Button type="button" size="sm" onClick={() => copyToClipboard(generatedKey)}>
                  <Copy className="h-4 w-4" />
                </Button>
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={closeCreateDialog}>
                  Close
                </Button>
              </DialogFooter>
            </>
          ) : (
            <>
              <DialogHeader>
                <DialogTitle>Create New API Key</DialogTitle>
                <DialogDescription>Give your new API key a descriptive name.</DialogDescription>
              </DialogHeader>
              <div className="grid gap-4 py-4">
                <div className="grid grid-cols-4 items-center gap-4">
                  <Label htmlFor="key-name" className="text-right">
                    Name
                  </Label>
                  <Input
                    id="key-name"
                    value={newKeyName}
                    onChange={(e: ChangeEvent<HTMLInputElement>) => setNewKeyName(e.target.value)}
                    className="col-span-3"
                    placeholder="e.g., My Production Server"
                    disabled={isGenerating}
                  />
                </div>
              </div>
              <DialogFooter>
                <DialogClose asChild>
                  <Button type="button" variant="outline" disabled={isGenerating}>
                    Cancel
                  </Button>
                </DialogClose>
                <Button
                  type="button"
                  onClick={handleGenerateKey}
                  disabled={isGenerating || !newKeyName.trim()}
                >
                  {isGenerating ? 'Generating...' : 'Generate Key'}
                </Button>
              </DialogFooter>
            </>
          )}
        </DialogContent>
      </Dialog>

      <Card className="mt-6">
        <CardContent>
          <p className="text-xs text-muted-foreground">
            API keys grant access to your account. Treat them like passwords and keep them secure.
          </p>
        </CardContent>
        <CardFooter className="border-t px-6 py-4">
          <CardDescription>
            Manage your API keys here. Be sure to store them securely, as they won&apos;t be shown
            again.
          </CardDescription>
        </CardFooter>
      </Card>
    </div>
  );
}

'use client';

import React, { useState, useTransition, useEffect } from 'react';
import { useSearchParams } from 'next/navigation';
import {
  linkStripeAccountServerAction,
  disconnectStripeAccountServerAction,
  resumePayoutsServerAction,
  pausePayoutsServerAction,
  toggleAlertsServerAction,
} from './actions';
import { Button } from '@/components/ui/button';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { Switch } from '@/components/ui/switch';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { toast } from 'sonner';
import { format, formatDistanceToNow } from 'date-fns';
import { Loader2, Trash2, Copy, PlusCircle, Info, BellOff, Clock } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { BackfillProgress } from '@/components/progress/BackfillProgress';

// Define the structure of a connected account based on fetched data
interface ConnectedAccount {
  id: string;
  stripe_account_id: string;
  business_name: string | null;
  created_at: string;
  payouts_paused: boolean;
  paused_by: string | null;
  paused_reason: string | null;
  alerts_muted_until: string | null;
}

const MUTE_OPTIONS = [
  { value: '60', label: '1 Hour' },
  { value: '360', label: '6 Hours' },
  { value: '1440', label: '24 Hours' },
  { value: 'infinity', label: 'Indefinitely' },
];

export function ConnectedAccountsManager({
  initialAccounts,
}: {
  initialAccounts: ConnectedAccount[];
}) {
  const [accounts, setAccounts] = useState<ConnectedAccount[]>(initialAccounts);
  const [isLinking, startLinkTransition] = useTransition();
  const [isDisconnecting, startDisconnectTransition] = useTransition();
  const [isTogglingPayouts, startPayoutsToggleTransition] = useTransition();
  const [togglingPayoutsAccountId, setTogglingPayoutsAccountId] = useState<string | null>(null);
  const [accountToDisconnect, setAccountToDisconnect] = useState<string | null>(null);
  const [muteDuration, setMuteDuration] = useState<string>('360');
  const [isTogglingMute, startMuteToggleTransition] = useTransition();
  const [togglingMuteAccountId, setTogglingMuteAccountId] = useState<string | null>(null);

  const searchParams = useSearchParams();

  // Update local state if initialAccounts changes (e.g., after revalidation)
  useEffect(() => {
    setAccounts(initialAccounts);
  }, [initialAccounts]);

  // Check for error flags from OAuth callback
  useEffect(() => {
    const error = searchParams.get('error');
    if (error === 'duplicate_link') {
      toast.error('This Stripe account is already linked.');
      window.history.replaceState(null, '', '/settings/connected-accounts');
    }
  }, [searchParams]);

  const handleAddAccount = () => {
    startLinkTransition(async () => {
      try {
        const result = await linkStripeAccountServerAction();
        if (result?.url) {
          window.location.href = result.url;
        } else {
          toast.error('Could not start Stripe connection. Please try again.');
        }
      } catch (error) {
        toast.error(error instanceof Error ? error.message : 'An unexpected error occurred.');
      }
    });
  };

  const handleDisconnect = (stripeAccountId: string) => {
    startDisconnectTransition(async () => {
      const previousAccounts = accounts;
      setAccounts((prev) => prev.filter((acc) => acc.stripe_account_id !== stripeAccountId));
      setAccountToDisconnect(null);
      try {
        await disconnectStripeAccountServerAction(stripeAccountId);
        toast.success('Stripe account disconnected successfully.');
      } catch (error) {
        setAccounts(previousAccounts);
        toast.error(error instanceof Error ? error.message : 'Could not disconnect account.');
      }
    });
  };

  const handleTogglePayouts = (account: ConnectedAccount) => {
    setTogglingPayoutsAccountId(account.stripe_account_id);
    startPayoutsToggleTransition(async () => {
      const action = account.payouts_paused ? resumePayoutsServerAction : pausePayoutsServerAction;
      const optimisticUpdate = !account.payouts_paused;
      const previousAccounts = accounts;

      // Optimistic UI Update
      setAccounts((prev) =>
        prev.map((acc) =>
          acc.stripe_account_id === account.stripe_account_id
            ? { ...acc, payouts_paused: optimisticUpdate }
            : acc,
        ),
      );

      try {
        await action(account.stripe_account_id);
        toast.success(`Payouts ${optimisticUpdate ? 'paused' : 'resumed'} successfully.`);
        // Data will refresh via revalidatePath
      } catch (error) {
        setAccounts(previousAccounts); // Rollback optimistic update
        toast.error(error instanceof Error ? error.message : 'Failed to update payout status.');
      } finally {
        setTogglingPayoutsAccountId(null);
      }
    });
  };

  const handleToggleMute = (account: ConnectedAccount) => {
    setTogglingMuteAccountId(account.stripe_account_id);
    startMuteToggleTransition(async () => {
      const isCurrentlyMuted =
        !!account.alerts_muted_until && new Date(account.alerts_muted_until) > new Date();
      const action = isCurrentlyMuted ? 'unmute' : 'mute';
      let durationMinutes: number | undefined = undefined;

      if (action === 'mute') {
        if (muteDuration === 'infinity') {
          durationMinutes = Infinity;
        } else {
          durationMinutes = parseInt(muteDuration, 10);
          if (isNaN(durationMinutes)) {
            console.warn('Invalid mute duration selected, defaulting to 6 hours');
            durationMinutes = 360; // Default if parse fails
          }
        }
      }

      const optimisticMutedUntil =
        action === 'mute'
          ? durationMinutes === Infinity
            ? 'infinity'
            : new Date(Date.now() + durationMinutes * 60000).toISOString()
          : null;

      // Optimistic UI update
      const previousAccounts = accounts;
      setAccounts((prev) =>
        prev.map((acc) =>
          acc.stripe_account_id === account.stripe_account_id
            ? { ...acc, alerts_muted_until: optimisticMutedUntil }
            : acc,
        ),
      );

      try {
        const result = await toggleAlertsServerAction({
          stripeAccountId: account.stripe_account_id,
          action,
          durationMinutes: action === 'mute' ? durationMinutes : undefined,
        });

        if (result.success) {
          toast.success(`Alerts ${action === 'mute' ? 'muted' : 'unmuted'} successfully.`);
          // Update with the exact value from the server if different (e.g., due to default fallback)
          setAccounts((prev) =>
            prev.map((acc) =>
              acc.stripe_account_id === account.stripe_account_id
                ? { ...acc, alerts_muted_until: result.mutedUntil }
                : acc,
            ),
          );
        } else {
          throw new Error('Server action failed'); // Should be caught below
        }
        // Data should revalidate via revalidatePath
      } catch (error) {
        setAccounts(previousAccounts); // Rollback
        toast.error(error instanceof Error ? error.message : `Failed to ${action} alerts.`);
      } finally {
        setTogglingMuteAccountId(null);
      }
    });
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard
      .writeText(text)
      .then(() => toast.success('Stripe ID copied to clipboard!'))
      .catch((_err) => toast.error('Failed to copy ID.'));
  };

  const getPauseTooltipContent = (account: ConnectedAccount): string => {
    if (account.payouts_paused) {
      let reason = `Paused by ${account.paused_by || 'unknown'}`;
      if (account.paused_reason) reason += `: ${account.paused_reason.replace(/_/g, ' ')}`;
      return reason;
    } else {
      return 'Automatic payouts active.';
    }
  };

  const getMuteStatus = (account: ConnectedAccount): { muted: boolean; text: string } => {
    if (account.alerts_muted_until) {
      if (account.alerts_muted_until === 'infinity') {
        return { muted: true, text: 'Muted indefinitely' };
      }
      const muteEndDate = new Date(account.alerts_muted_until);
      if (muteEndDate > new Date()) {
        return {
          muted: true,
          text: `Muted ${formatDistanceToNow(muteEndDate, { addSuffix: true })}`,
        };
      }
    }
    return { muted: false, text: 'Alerts active' };
  };

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between">
        <div>
          <CardTitle>Connected Stripe Accounts</CardTitle>
          <CardDescription>
            Link multiple Stripe accounts to manage them with DOCK108.
          </CardDescription>
        </div>
        <Button onClick={handleAddAccount} disabled={isLinking}>
          {isLinking ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" /> Connecting...
            </>
          ) : (
            <>
              <PlusCircle className="mr-2 h-4 w-4" /> Add Account
            </>
          )}
        </Button>
      </CardHeader>
      <CardContent>
        {accounts.length === 0 ? (
          <p className="text-center text-muted-foreground py-8">
            No Stripe accounts connected yet.
          </p>
        ) : (
          <TooltipProvider delayDuration={300}>
            {/* Desktop Table */}
            <div className="hidden md:block">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Display Name</TableHead>
                    <TableHead>Stripe Account ID</TableHead>
                    <TableHead>Payouts</TableHead>
                    <TableHead>Alerts</TableHead>
                    <TableHead>Backfill Status</TableHead>
                    <TableHead>Connected On</TableHead>
                    <TableHead className="text-right">Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {accounts.map((account) => {
                    const muteStatus = getMuteStatus(account);
                    const isLoadingMute =
                      isTogglingMute && togglingMuteAccountId === account.stripe_account_id;
                    const isLoadingPayouts =
                      isTogglingPayouts && togglingPayoutsAccountId === account.stripe_account_id;
                    return (
                      <TableRow key={account.id}>
