  try {
    // Attempt to fetch the specific settings row
    const { data, error } = await supabase
      .from('settings')
      .select('*')
      .eq('id', SETTINGS_ROW_ID) // Use the constant ID
      .maybeSingle(); // Use maybeSingle as it might not exist initially

    if (error) throw error;

