'use client';

import { useDemoScenario } from './useDemoScenario';
import { EventTable } from '../../components/guardian-demo/EventTable';
import ActionLog from '../../components/guardian-demo/ActionLog';
import SlackAlert from '../../components/guardian-demo/SlackAlert';
import { ScenarioPicker } from '../../components/guardian-demo/ScenarioPicker';
import { getScenarios } from './getScenarios';
import { useState, useEffect } from 'react';
// import { AlertTable } from './AlertTable';
// import { PayoutTable } from './PayoutTable';
// import { Timeline } from './Timeline';
// import { log } from '@/lib/logger';
import toast from 'react-hot-toast';

// Get scenarios at build time
const scenarioList = getScenarios();
const scenarioIds = scenarioList.map((s) => s.id);
const scenarioLabels = scenarioList.reduce<Record<string, string>>((acc, s) => {
  acc[s.id] = s.label;
  return acc;
}, {});

export function DemoViewer() {
  // Default to the first scenario instead of empty string
  const [scenario, setScenario] = useState<string>(scenarioIds.length > 0 ? scenarioIds[0] : '');
  const [speed, setSpeed] = useState(1);

  // We don't need fallback events anymore since we always use scenarios
  const scenarioData = useDemoScenario(scenario, {
    onExpire: () => handleReset(true),
    speed,
  });

  // Use scenario events
  const { events, isRunning, totalDelayMs } = scenarioData;

  const [log, setLog] = useState<string[]>(['Monitoring started…']);
  const [alert, setAlert] = useState<{ text: string }>();

  // Check for scenario completion
  useEffect(() => {
    // If the scenario was running but now stopped and has played all events
    if (!isRunning && events.length > 0 && events.length === scenarioData.total) {
      toast.success('Scenario complete. Click Restart to replay.');
    }
  }, [isRunning, events.length, scenarioData.total]);

  function handleReset(auto = false) {
    scenarioData.restart();
    setLog([auto ? 'Demo auto‑restarted after 5 min idle.' : 'Monitoring restarted…']);
    setAlert(undefined);
  }

  const handleScenarioChange = (newScenario: string) => {
    setScenario(newScenario);
    handleReset(false);
  };

  // Update the log message based on the scenario type
  useEffect(() => {
    const latest = events[events.length - 1];
    if (!latest?.flagged) return;

    const amt = (latest.amount ?? 0) / 100;
    let logMessage = `⚠️ Fraud detected in payout ${latest.id.slice(0, 8)}…`;
    let alertMessage = `🚨 Payout auto‑paused: $${amt.toFixed(2)} (${latest.id.slice(0, 8)}…)`;

    // Different messages based on scenario type
    if (scenario === 'velocity-breach') {
      logMessage = `⚠️ Velocity breach detected — 3 payouts in under 60s.`;
      alertMessage += ` – velocity breach`;
    } else if (scenario === 'bank-swap') {
      logMessage = `⚠️ Bank account swap detected — new account added recently.`;
      alertMessage += ` – suspicious bank account change`;
    } else if (scenario === 'geo-mismatch') {
      logMessage = `⚠️ Geo-location mismatch detected — payout from unusual location.`;
      alertMessage += ` – unusual location`;
    }

    setLog((l) =>
      [logMessage, `⏸ Auto-pause triggered for payout ${latest.id.slice(0, 8)}…`, ...l].slice(
        0,
        20,
      ),
    );

    setAlert({
      text: alertMessage,
    });
  }, [events, scenario]);

  return (
    <>
      <div className="mt-4">
        <ScenarioPicker
          scenarios={scenarioIds}
          scenarioLabels={scenarioLabels}
          currentScenario={scenario}
          onChange={handleScenarioChange}
          onRestart={() => handleReset(false)}
          speed={speed}
          onSpeedChange={setSpeed}
        />
      </div>
      <section className="mt-4 grid gap-6 lg:grid-cols-[2fr_1fr]">
        <EventTable events={events} className="w-full" />
        <div>
          <h2 className="mb-2 text-lg font-semibold">Action Log</h2>
          <ActionLog entries={log} />
          <SlackAlert alert={alert} />
        </div>
      </section>
    </>
  );
}

/**
 * Represents a single event in a fraud scenario
 */
export type ScenarioEvent = {
  /** Milliseconds to wait after the previous event before firing this one */
  delayMs: number;

  /** Stripe event type */
  type: string;

  /** Event payload data (Stripe object) */
  payload: {
    id: string;
    object: string;
    [key: string]: any;
  };
};

/**
 * A complete fraud scenario consisting of a sequence of events
 */
export type Scenario = ScenarioEvent[];

'use client';

import { useEffect, useRef, useState, useCallback } from 'react';
import { DemoEvent } from './useFakeStripeEvents';

export type ScenarioEvent = {
  delayMs: number;
  type: 'account.updated' | 'payout.paid';
  payload: Record<string, any>;
};

export type ScenarioOptions = {
  onExpire?: () => void;
  speed?: number;
};

export function useDemoScenario(scenarioName: string | null, options: ScenarioOptions = {}) {
  const { onExpire, speed = 1 } = options;
  const [events, setEvents] = useState<DemoEvent[]>([]);
  const [scenarioEvents, setScenarioEvents] = useState<ScenarioEvent[]>([]);
  const [currentIndex, setCurrentIndex] = useState<number>(0);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [isRunning, setIsRunning] = useState<boolean>(false);
  const [totalDelayMs, setTotalDelayMs] = useState<number>(0);

  const timersRef = useRef<NodeJS.Timeout[]>([]);
  const startRef = useRef<number>(Date.now());
  const fetchControllerRef = useRef<AbortController | null>(null);
  const pendingEventsRef = useRef<
    Array<{ event: ScenarioEvent; index: number; scheduledAt: number }>
  >([]);

  // Clear all timers on unmount or reset
  const clearAllTimers = useCallback(() => {
    if (timersRef.current.length > 0) {
      timersRef.current.forEach(clearTimeout);
      timersRef.current = [];
    }
    pendingEventsRef.current = [];
    setIsRunning(false);
  }, []);

  const reset = useCallback(() => {
    clearAllTimers();
    setEvents([]);
    setCurrentIndex(0);
    startRef.current = Date.now();
  }, [clearAllTimers]);

  const restart = (newScenarioName?: string) => {
    reset();
    if (newScenarioName) {
      loadScenario(newScenarioName);
    } else if (scenarioName) {
      scheduleEvents();
    }
  };

  // Load scenario data
  const loadScenario = useCallback(
    async (name: string) => {
      if (!name) return;

      // Cancel any pending fetch
      if (fetchControllerRef.current) {
        fetchControllerRef.current.abort();
      }

      // Create new controller for this fetch
      fetchControllerRef.current = new AbortController();
      const signal = fetchControllerRef.current.signal;

      setIsLoading(true);
      setError(null);

      try {
        const response = await fetch(`/guardian-demo/scenarios/${name}.json`, { signal });
        if (!response.ok) {
          throw new Error(`Failed to load scenario: ${response.statusText}`);
        }

        const data: ScenarioEvent[] = await response.json();
        setScenarioEvents(data);

        // Calculate total delay time from all events
        const total = data.reduce((sum, event) => sum + event.delayMs, 0);
        setTotalDelayMs(total);

        setIsLoading(false);
        setCurrentIndex(0);

        // Clear any existing events and timers before scheduling new ones
        reset();
      } catch (err) {
        // Don't set error if aborted
        if (err instanceof Error && err.name === 'AbortError') {
          return;
        }
        setError(err instanceof Error ? err.message : 'Failed to load scenario');
        setIsLoading(false);
      }
    },
    [reset],
  );

  // Schedule the events based on the scenario
  const scheduleEvents = useCallback(() => {
    clearAllTimers();
    const now = Date.now();
    startRef.current = now;
    setIsRunning(true);

    // Track pending events with information about when they were scheduled
    pendingEventsRef.current = scenarioEvents.map((event, index) => ({
      event,
      index,
      scheduledAt: now,
    }));

    // Schedule all events
    scenarioEvents.forEach((event, index) => {
      const timer = setTimeout(
        () => {
          // Generate a truly unique ID by including a timestamp
          const uniqueId = `${event.payload.id || 'event'}-${event.type}-${index}-${Date.now()}`;

          // Convert scenario event to DemoEvent
          const demoEvent: DemoEvent = {
            id: uniqueId,
            type: event.type,
            amount: event.type === 'payout.paid' ? event.payload.amount : undefined,
            created: Date.now(),
            flagged:
              event.payload.flagged === true ||
              (event.payload.metadata && event.payload.metadata.guardian_action === 'paused'),
          };

          // Remove this event from pending events ref
          pendingEventsRef.current = pendingEventsRef.current.filter(
            (pe) => !(pe.index === index && pe.event === event),
          );

          setEvents((prev) => [...prev.slice(-49), demoEvent]);
          setCurrentIndex(index + 1);

          // If this is the last event, stop the scenario and set timer to expire after 5 minutes
          if (index === scenarioEvents.length - 1) {
            setIsRunning(false);

            const expireTimer = setTimeout(() => {
              reset();
              onExpire?.();
            }, 300_000); // 5 minutes

            timersRef.current.push(expireTimer);
          }
        },
        Math.max(0, event.delayMs / speed),
      );

      timersRef.current.push(timer);
    });
  }, [clearAllTimers, scenarioEvents, speed, onExpire, reset]);

  // Reschedule events when speed changes
  const rescheduleEvents = useCallback(() => {
    if (pendingEventsRef.current.length === 0) return;

    clearAllTimers();
    const now = Date.now();
    const elapsed = now - startRef.current;
    setIsRunning(true);

    // For each pending event, calculate the new delay based on elapsed time
    pendingEventsRef.current.forEach(({ event, index }) => {
      const originalDelay = event.delayMs;
      // Adjust delay based on elapsed time
      const adjustedDelay = Math.max(0, originalDelay - elapsed);

      // Schedule with new delay
      const timer = setTimeout(
        () => {
          // Generate a truly unique ID by including a timestamp
          const uniqueId = `${event.payload.id || 'event'}-${event.type}-${index}-${Date.now()}`;

          // Convert scenario event to DemoEvent
          const demoEvent: DemoEvent = {
            id: uniqueId,
            type: event.type,
            amount: event.type === 'payout.paid' ? event.payload.amount : undefined,
            created: Date.now(),
            flagged:
              event.payload.flagged === true ||
              (event.payload.metadata && event.payload.metadata.guardian_action === 'paused'),
          };

          // Remove this event from pending events ref
          pendingEventsRef.current = pendingEventsRef.current.filter(
            (pe) => !(pe.index === index && pe.event === event),
          );

          setEvents((prev) => [...prev.slice(-49), demoEvent]);
          setCurrentIndex(index + 1);

          // If this is the last event, stop running and set timer to expire
          if (index === scenarioEvents.length - 1) {
            setIsRunning(false);

            const expireTimer = setTimeout(() => {
              reset();
              onExpire?.();
            }, 300_000); // 5 minutes

            timersRef.current.push(expireTimer);
          }
        },
        Math.max(0, adjustedDelay / speed),
      );

      timersRef.current.push(timer);
    });
  }, [clearAllTimers, scenarioEvents, speed, onExpire, reset]);

  // Load scenario effect
  useEffect(() => {
    if (scenarioName) {
      clearAllTimers();
      loadScenario(scenarioName);
    }

    return () => {
      if (fetchControllerRef.current) {
        fetchControllerRef.current.abort();
        fetchControllerRef.current = null;
      }
      clearAllTimers();
    };
  }, [scenarioName, clearAllTimers, loadScenario]);

  // Schedule events when scenario data is loaded
  useEffect(() => {
    if (scenarioEvents.length > 0) {
      scheduleEvents();
    }
  }, [scenarioEvents, scheduleEvents]);

  // Reschedule when speed changes
  useEffect(() => {
    if (scenarioEvents.length > 0 && pendingEventsRef.current.length > 0) {
      rescheduleEvents();
    }
  }, [speed, scenarioEvents.length, rescheduleEvents]);

  return {
    events,
    currentIndex,
    total: scenarioEvents.length,
    isLoading,
    error,
    restart,
    reset,
    isRunning,
    totalDelayMs,
  };
}

'use client';

import { Suspense, useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { Loader2 } from 'lucide-react';
import { createClient } from '@/utils/supabase/client';
import Link from 'next/link';

import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';

// Client component that safely uses useSearchParams
function LoginForm() {
  const router = useRouter();
  const searchParams = useSearchParams();
  const redirectTo = searchParams.get('redirectTo');
  const defaultRedirect = '/stripe-guardian/alerts';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const supabase = createClient();

  useEffect(() => {
    // Check if already logged in and redirect to dashboard
    const checkSession = async () => {
      const {
        data: { session },
      } = await supabase.auth.getSession();
      if (session) {
        router.replace('/stripe-guardian/alerts');
      }
    };

    checkSession();
  }, [router, supabase]);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const { error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        throw error;
      }

      // Redirect after successful login
      router.push(redirectTo || defaultRedirect);
      router.refresh();
    } catch (error: any) {
      setError(error.message || 'An error occurred during sign in');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-slate-900 p-4">
      <Card className="w-full max-w-md bg-slate-800 text-white">
        <CardHeader className="space-y-1">
          <CardTitle className="text-2xl font-bold">Sign in to DOCK108</CardTitle>
          <CardDescription className="text-slate-400">
            Enter your email and password to access your account
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleLogin} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                placeholder="email@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="bg-slate-700 border-slate-600"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="bg-slate-700 border-slate-600"
              />
            </div>
            {error && <div className="text-red-500 text-sm">{error}</div>}
            <Button type="submit" disabled={loading} className="w-full">
              {loading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Signing in...
                </>
              ) : (
                'Sign in'
              )}
            </Button>
          </form>
        </CardContent>
        <CardFooter className="flex flex-col space-y-2">
          <div className="text-center text-sm text-slate-400">
            Don&apos;t have an account?
            <Link href="/sign-up" className="ml-1 font-medium text-blue-400 hover:underline">
              Sign up for Beta
            </Link>
          </div>
        </CardFooter>
      </Card>
    </div>
  );
}

// Main page component with Suspense boundary
export default function LoginPage() {
  return (
    <Suspense
      fallback={
        <div className="flex min-h-screen items-center justify-center bg-slate-900">
          <Loader2 className="h-8 w-8 animate-spin text-primary" />
        </div>
      }
    >
      <LoginForm />
    </Suspense>
  );
}

